import React, { useState, useEffect, useContext, useRef } from "react";
import { Link, useNavigate } from "react-router-dom";
import { FaUserCircle, FaHeart, FaShoppingCart } from "react-icons/fa";
import "../styles/Navbar.css";
import { UserContext } from "../context/UserContext";
import { FavoritesContext } from "../context/FavoritesContext";
import { CartContext } from "../context/CartContext";

const Navbar = ({ onSearch }) => {
  const { user, logout } = useContext(UserContext);
  const { favorites, clearFavorites } = useContext(FavoritesContext);
  const { cart, clearCart } = useContext(CartContext);
  const navigate = useNavigate();
  const [menuOpen, setMenuOpen] = useState(false);
  const [searchTerm, setSearchTerm] = useState("");
  const menuRef = useRef(null);

  const handleSearch = (e) => {
    setSearchTerm(e.target.value);
    onSearch(e.target.value);
  };

  const handleLogout = () => {
    clearCart();
    clearFavorites();
    logout();
    navigate("/login");
  };

  // Gestion du clic en dehors du menu
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (menuRef.current && !menuRef.current.contains(event.target)) {
        setMenuOpen(false);
      }
    };
    document.addEventListener("mousedown", handleClickOutside);
    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };
  }, []);

  return (
    <nav className="navbar">
      <button
  className="filter-toggle-btn"
  onClick={() => navigate("/BeautyPage?showFilters=true")}
>
  Filtres
</button>


      <Link to="/" className="logo">Lyséa</Link>
      <div className="nav-links">
        <Link to="/BeautyPage">BEAUTÉ</Link>
        <Link to="/lifestyle">LIFESTYLE</Link>
        <Link to="/blog">BLOG</Link>
      </div>

      <div className="search-bar">
        <input
          type="text"
          placeholder="Rechercher..."
          value={searchTerm}
          onChange={handleSearch}
        />
      </div>

      <div className="nav-icons">
        <Link to="/favorites" className="fav-link">
          <FaHeart className="icon" />
          {favorites.length > 0 && <span className="fav-badge">{favorites.length}</span>}
        </Link>

        <Link to="/cart" className="cart-link">
          <FaShoppingCart className="icon" />
          {cart.length > 0 && <span className="cart-badge">{cart.length}</span>}
        </Link>

        {user && <span className="welcome-text">Bonjour, {user.nom}</span>}
        {user && user.role === "admin" && <Link to="/admin" className="admin-link">Admin</Link>}

        <div className="user-menu" ref={menuRef}>
          <FaUserCircle 
            className="icon" 
            onClick={() => setMenuOpen((prev) => !prev)} 
          />
          {menuOpen && (
            <div className="dropdown">
              {user ? (
                <button onClick={handleLogout} className="logout-btn">Déconnecter</button>
              ) : (
                <>
                  <Link to="/login" className="dropdown-item" onClick={() => setMenuOpen(false)}>Sign In</Link>
                  <Link to="/register" className="dropdown-item" onClick={() => setMenuOpen(false)}>Register Now</Link>
                </>
              )}
            </div>
          )}
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
