import React, { useState, useContext, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { CartContext } from "../context/CartContext";
import { UserContext } from "../context/UserContext";
import { PayPalScriptProvider, PayPalButtons } from "@paypal/react-paypal-js";
import "../styles/OrderPage.css";

const OrderPage = () => {
    const { cart, clearCart } = useContext(CartContext);
    const { user } = useContext(UserContext);
    const [address, setAddress] = useState(null);
    const [firstName, setFirstName] = useState("");
    const [lastName, setLastName] = useState("");
    const [phone, setPhone] = useState("");
    const [addressText, setAddressText] = useState("");
    const [extraInfo, setExtraInfo] = useState("");
    const [postalCode, setPostalCode] = useState("");
    const [defaultAddress, setDefaultAddress] = useState(false);
    const [isAddressSaved, setIsAddressSaved] = useState(false);
    const [showPayment, setShowPayment] = useState(false);
    const [paymentMethod, setPaymentMethod] = useState("");  // âœ… Pour le choix du paiement
    const navigate = useNavigate();

    const totalAmount = cart.reduce((acc, item) => acc + item.prix * item.quantity, 0);

    useEffect(() => {
        if (user) {
            axios.get(`http://localhost:5001/api/addresses/${user.id}`)
                .then((response) => setAddress(response.data))
                .catch((error) => console.error("Erreur chargement de l'adresse :", error));
        }
    }, [user]);

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const data = {
                user_id: user.id,
                first_name: firstName,
                last_name: lastName,
                phone: phone,
                address: addressText,
                extra_info: extraInfo,
                postal_code: postalCode,
                is_default: defaultAddress ? 1 : 0,
            };
            await axios.post("http://localhost:5001/api/addresses/save", data, {
                headers: {
                    "Content-Type": "application/json"
                },
                withCredentials: true
            });

            alert("Adresse enregistrÃ©e !");
            setIsAddressSaved(true);
            setShowPayment(false);  // Masquer le paiement avant de choisir
        } catch (error) {
            console.error("âŒ Erreur lors de l'enregistrement :", error);
            alert("Erreur lors de l'enregistrement de l'adresse");
        }
    };

    const handlePaymentSuccess = (details) => {
        alert("Paiement rÃ©ussi !");
        clearCart();
        navigate("/");
    };

    const handlePaymentMethod = (method) => {
        setPaymentMethod(method);
        setShowPayment(true);
    };

    return (
        <div className="order-page">
            <h2>Passer Ã  la caisse ðŸ‡¨ðŸ‡¦</h2>
            <div className="order-container">
                <div className="order-left">
                    {!isAddressSaved && (
                        <>
                            <h3>Adresse de livraison</h3>
                            <form onSubmit={handleSubmit}>
                                <div className="form-row">
                                    <input type="text" placeholder="PrÃ©nom*" value={firstName} onChange={(e) => setFirstName(e.target.value)} required />
                                    <input type="text" placeholder="Nom de famille*" value={lastName} onChange={(e) => setLastName(e.target.value)} required />
                                </div>
                                <input type="tel" placeholder="TÃ©lÃ©phone*" value={phone} onChange={(e) => setPhone(e.target.value)} required />
                                <input type="text" placeholder="Adresse*" value={addressText} onChange={(e) => setAddressText(e.target.value)} required />
                                <input type="text" placeholder="Code postal*" value={postalCode} onChange={(e) => setPostalCode(e.target.value)} required />
                                <div className="checkbox-row">
                                    <input type="checkbox" checked={defaultAddress} onChange={(e) => setDefaultAddress(e.target.checked)} />
                                    <label>DÃ©finir comme l'adresse d'expÃ©dition par dÃ©faut</label>
                                </div>
                                <button type="submit" className="save-btn">Enregistrer et continuer</button>
                            </form>
                        </>
                    )}
                </div>

                {isAddressSaved && (
                    <div className="order-right">
                        <h3>Total de la commande</h3>
                        <p>Sous-total : {totalAmount.toFixed(2)} CAD</p>
                        <p>Livraison : GRATUIT</p>
                        <p>Taxes : {(totalAmount * 0.15).toFixed(2)} CAD</p>
                        <p>Total estimÃ© : {(totalAmount * 1.15).toFixed(2)} CAD</p>

                        <h4>Choisissez votre mÃ©thode de paiement :</h4>
                        <button onClick={() => handlePaymentMethod("paypal")}>PayPal</button>
                        <button onClick={() => handlePaymentMethod("stripe")}>Stripe</button>

                        {showPayment && paymentMethod === "paypal" && (
                            <PayPalScriptProvider options={{ "client-id": "VOTRE_CLIENT_ID_PAYPAL" }}>
                                <PayPalButtons
                                    style={{ layout: "vertical" }}
                                    createOrder={(data, actions) => {
                                        return actions.order.create({
                                            purchase_units: [{
                                                amount: {
                                                    value: (totalAmount * 1.15).toFixed(2),
                                                    currency_code: "CAD"
                                                }
                                            }]
                                        });
                                    }}
                                    onApprove={(data, actions) => {
                                        return actions.order.capture().then((details) => {
                                            handlePaymentSuccess(details);
                                        });
                                    }}
                                    onError={(err) => {
                                        console.error("Erreur de paiement :", err);
                                        alert("Le paiement a Ã©chouÃ©, veuillez rÃ©essayer.");
                                    }}
                                />
                            </PayPalScriptProvider>
                        )}

                        {showPayment && paymentMethod === "stripe" && (
                            <div>
                                <p>ðŸ”’ Paiement sÃ©curisÃ© avec Stripe Ã  venir...</p>
                                {/* Stripe integration can be added here */}
                            </div>
                        )}
                    </div>
                )}
            </div>
        </div>
    );
};

export default OrderPage;
