import React from "react";
import { useNavigate } from "react-router-dom";
import "../styles/HomePage.css"; // Fichier CSS

const brands = [
  {
    id: "Karine Joncas",
    name: "Karine Joncas",
    description: "Dès son plus jeune âge, la Lavalloise Karine Joncas concocte des soins de beauté maison...",
    image: "/images/karine_joncas.jpg",
  },
  {
    id: "Watier",
    name: "Watier",
    description: "Qui ne connaît pas cet emblème des produits cosmétiques québécois ?...",
    image: "/images/watier.jpg",
  },
  {
    id: "Reversa",
    name: "Reversa",
    description: "Reversa est une marque bien de chez nous, fondée par Robert Lavoie et Nicole Clavet...",
    image: "/images/reversa.jpg",
  },
];

const HomePage = () => {
  const navigate = useNavigate();

  return (
    <div className="homepage-container">
      <h1>Bienvenue sur Cosmétique Québec</h1>
      <div className="brands-section">
        {brands.map((brand) => (
          <div key={brand.id} className="brand-card" onClick={() => navigate(`/brands/${brand.id}`)}>
            <img src={brand.image} alt={brand.name} className="brand-image" />
            <div className="brand-info">
              <h3>{brand.name}</h3>
              <p>{brand.description}</p>
              <button className="brand-button">Voir la marque</button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};



export default HomePage;
