import React, { useState, useContext } from "react";
import { UserContext } from "../context/UserContext";

const PayPalButton = ({ amount }) => {
    const [loading, setLoading] = useState(false);
    const { user } = useContext(UserContext); // pour r√©cup√©rer l'ID de l'utilisateur

    const handlePayment = async () => {
        setLoading(true);
        try {
            const response = await fetch("http://localhost:5001/api/checkout/create-payment", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({ amount })
                   
            });

             if (!response.ok) {
        const text = await response.text();
        console.error("Create-payment failed:", text);
        alert("Erreur lors de la cr√©ation du paiement.");
        setLoading(false);
        return;
      }
            const data = await response.json();
            if (data.approvalUrl) {
                window.location.href = data.approvalUrl; // üîÅ redirection vers PayPal
            } else {
                alert("Lien d‚Äôapprobation PayPal introuvable.");
            }
        } catch (error) {
            console.error("‚ùå Erreur PayPal :", error);
            alert("Erreur lors du paiement.");
        }
        setLoading(false);
    };

    return (
        <button onClick={handlePayment} className="btn-paypal" disabled={loading}>
            {loading ? "Chargement..." : "Payer avec PayPal"}
        </button>
    );
};

export default PayPalButton;
