import React, { createContext, useState, useContext, useEffect } from "react";
import axios from "axios";
import { UserContext } from "./UserContext";

export const CartContext = createContext();

export const CartProvider = ({ children }) => {
    const { user } = useContext(UserContext);
    const [cart, setCart] = useState([]);

    // Charger le panier
    useEffect(() => {
        if (user) {
            axios.get(`http://localhost:5001/api/cart/${user.id}`)
                .then((response) => setCart(response.data))
                .catch((error) => console.error("Erreur de chargement du panier :", error));
        }
    }, [user]);

    // Ajouter au panier
    const addToCart = async (product) => {
        try {
            await axios.post("http://localhost:5001/api/cart/add", {
                user_id: user.id,
                product_id: product.id,
                quantity: 1,
            });
            setCart((prev) => [...prev, { ...product, quantity: 1 }]);
        } catch (error) {
            console.error("Erreur d'ajout au panier :", error);
        }
    };

    const removeFromCart = async (productId) => {
        try {
            await axios.delete(`http://localhost:5001/api/cart/remove/${user.id}/${productId}`);
            setCart((prevCart) => prevCart.filter((item) => item.id !== productId));
        } catch (error) {
            console.error("❌ Erreur lors de la suppression du produit du panier :", error);
        }
    };
    

const clearCart = async () => {
    try {
        await axios.delete(`http://localhost:5001/api/cart/clear/${user.id}`);
        setCart([]);
    } catch (error) {
        console.error("❌ Erreur lors du vidage du panier :", error);
    }
};

const decreaseQuantity = async (productId) => {
    try {
        await axios.put(`http://localhost:5001/api/cart/decrease/${user.id}/${productId}`);
        setCart((prev) =>
            prev.map((item) =>
                item.id === productId && item.quantity > 1
                    ? { ...item, quantity: item.quantity - 1 }
                    : item
            )
        );
        console.log("Quantité diminuée avec succès !");
    } catch (error) {
        console.error("Erreur de diminution :", error);
    }
};
s


    return (
        <CartContext.Provider value={{ cart, addToCart, removeFromCart, clearCart, decreaseQuantity }}>
            {children}
        </CartContext.Provider>
    );
};
