import { useState } from "react";

export default function NewsletterForm() {
  const [email, setEmail] = useState("");
  const [loading, setLoading] = useState(false);
  const [msg, setMsg] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMsg(null);

    const valid = /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email);
    if (!valid) {
      setMsg({ type: "error", text: "⚠️ Adresse e-mail invalide." });
      return;
    }

    setLoading(true);
    try {
      const res = await fetch(
        `${process.env.REACT_APP_API_URL || "http://localhost:5001"}/api/newsletter`,
        {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ email }),
        }
      );

      if (res.ok) {
        setMsg({ type: "success", text: "✅ Merci ! Vous êtes inscrit(e) à l’infolettre." });
        setEmail("");
      } else {
        const data = await res.json().catch(() => ({}));
        setMsg({
          type: "error",
          text: data.error || "❌ Une erreur est survenue. Réessayez.",
        });
      }
    } catch (err) {
      setMsg({ type: "error", text: "❌ Impossible de contacter le serveur." });
    } finally {
      setLoading(false);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="newsletter-form">
      <label htmlFor="newsletter-email" className="newsletter-label">
        Recevez nos offres spéciales et nouveautés :
      </label>

      <div className="newsletter-input-group">
        <input
          id="newsletter-email"
          type="email"
          placeholder="Votre e-mail"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          className="newsletter-input"
          required
        />

        <button
          type="submit"
          disabled={loading || !email}
          className="newsletter-btn"
        >
          {loading ? "Envoi..." : "Rejoindre"}
        </button>
      </div>

      {msg && (
        <p
          className={`newsletter-msg ${
            msg.type === "success" ? "newsletter-success" : "newsletter-error"
          }`}
        >
          {msg.text}
        </p>
      )}
    </form>
  );
}
