import React, { useState, useEffect, useContext } from "react";
import axios from "axios";
import ProductCard from "../components/ProductCard";
import "../styles/BeautyPage.css";
import { useNavigate } from "react-router-dom";
import { FavoritesContext } from "../context/FavoritesContext";
import { CartContext } from "../context/CartContext";

const BeautyPage = ({ searchTerm }) => {
    const [products, setProducts] = useState([]);
    const [loading, setLoading] = useState(true);
    const navigate = useNavigate();
    const { favorites, toggleFavorite } = useContext(FavoritesContext);
    const { addToCart } = useContext(CartContext); // ✅ Utilisation correcte du contexte CartContext

    useEffect(() => {
        axios
            .get("http://localhost:5001/api/products")
            .then((response) => {
                setProducts(response.data);
                setLoading(false);
            })
            .catch((error) => {
                console.error("❌ Erreur chargement des produits :", error.message);
                setLoading(false);
            });
    }, []);
   // Filtrer les produits selon le terme de recherche
const filteredProducts = products.filter((product) =>
    product.nom && product.nom.toLowerCase().includes(searchTerm.toLowerCase())
);


    return (
        <div className="beauty-page">
            <h2 className="section-title">Nos Produits Beauté</h2>

            {loading ? (
                <p>Chargement des produits...</p>
            ) : products.length > 0 ? (
                <div className="product-grid">
                    {filteredProducts.map((product) => (
                        <ProductCard 
                            key={product.id} 
                            product={product} 
                            addToCart={() => addToCart(product)} // ✅ Correction ici
                            toggleLike={() => toggleFavorite(product)} 
                            isLiked={favorites.some((fav) => fav.id === product.id)}
                            navigate={() => navigate(`/product/${product.id}`)}
                        />
                    ))}
                </div>
            ) : (
                <p className="no-products">Aucun produit disponible.</p>
            )}
        </div>
    );
};

export default BeautyPage;
