import React, { useState, useContext } from "react";
import { Link, useNavigate } from "react-router-dom";
import { FaUserCircle, FaHeart, FaShoppingCart } from "react-icons/fa";
import "../styles/Navbar.css";
import { UserContext } from "../context/UserContext";
import { FavoritesContext } from "../context/FavoritesContext";
import { CartContext } from "../context/CartContext";

const Navbar = ({ onSearch }) => {
    const { user, logout } = useContext(UserContext);
    const { favorites } = useContext(FavoritesContext);
    const { cart } = useContext(CartContext);
    const [searchTerm, setSearchTerm] = useState("");
    const [menuOpen, setMenuOpen] = useState(false);
    const navigate = useNavigate();

    const handleSearch = (event) => {
        const term = event.target.value;
        setSearchTerm(term);
        if (onSearch) {
            onSearch(term);
        }
    };

    const handleLogout = () => {
        logout();
        setMenuOpen(false);
        navigate("/login");
    };

    return (
      <nav className="navbar">
      <Link to="/" className="logo">LYS√âA</Link>
  
      <div className="nav-links">
          <Link to="/BeautyPage">Beaut√©</Link>
          <Link to="/lifestyle">Lifestyle</Link>
          <Link to="/blog">Blog</Link>
      </div>
  
      <div className="nav-icons">
          <div className="search-bar">
              <input type="text" placeholder="Rechercher..." />
              <button>üîç</button>
          </div>
          <Link to="/favorites">
              <FaHeart className="icon" />
          </Link>
          <Link to="/cart">
              <FaShoppingCart className="icon" />
          </Link>
          <div className="user-info">
              {user ? (
                  <>
                      <p>Bienvenue, {user.nom}</p>
                      <div className="user-menu">
                          <FaUserCircle className="icon" />
                          <div className="dropdown">
                              <button onClick={handleLogout}>D√©connexion</button>
                          </div>
                      </div>
                  </>
              ) : (
                  <Link to="/login" className="icon">Se connecter</Link>
              )}
          </div>
      </div>
  </nav>
  
    );
};

export default Navbar;
