import React, { useContext, useEffect } from "react"; 
import { CartContext } from "../context/CartContext";
import { UserContext } from "../context/UserContext"; // ‚úÖ R√©cup√©ration de l'utilisateur
import { FaTrash } from "react-icons/fa";
import "../styles/CartPage.css"; 

const CartPage = () => {
    const { cart, addToCart, removeFromCart, clearCart, decreaseQuantity, checkout } = useContext(CartContext);
    console.log("üõí Contenu du panier :", cart);
    const { user } = useContext(UserContext); // ‚úÖ V√©rification utilisateur

    // ‚úÖ V√©rification du panier en console pour debug
    useEffect(() => {
        console.log("üì¶ Produits dans le panier :", cart);
    }, [cart]);

    const totalPrice = cart.reduce((acc, product) => acc + product.prix * product.quantity, 0);

    return (
        <div className="cart-container">
            <h2>üõí Mon Panier</h2>

            {cart.length === 0 ? (
                <p>Votre panier est vide.</p>
            ) : (
                <>
                    <ul>
                        {cart.map((product) => (
                            <li key={product.id} className="cart-item">
                                {/* ‚úÖ V√©rifie si l'image existe */}
                                {product.image_url && (
                                    
                                    <img src={`http://localhost:5000${product.image_url}`} alt={product.nom} />
                                )}
                                <div>
                                    <h3>{product.nom}</h3>
                                    <p>{product.prix} CAD</p>

                                    <div className="quantity-controls">
                                        <button onClick={() => decreaseQuantity(product.id)}>-</button>
                                        <span>{product.quantity}</span>
                                        <button onClick={() => addToCart(product)}>+</button>
                                    </div>

                                    <p><strong>Total : {(product.prix * product.quantity).toFixed(2)} CAD</strong></p>

                                    <button className="remove-btn" onClick={() => removeFromCart(product.id)}>
                                        <FaTrash /> Retirer
                                    </button>
                                </div>
                            </li>
                        ))}
                    </ul>

                    <h3 className="total-price">üí∞ Total √† payer : {totalPrice.toFixed(2)} CAD</h3>

                    <button className="clear-cart" onClick={clearCart}>üóëÔ∏è Vider le panier</button>

                    {/* ‚úÖ V√©rifie si l'utilisateur est connect√© avant d'afficher "Passer la commande" */}
                    {user ? (
                        <button className="checkout-btn" onClick={() => checkout(user.id)}>
                            ‚úÖ Passer la commande
                        </button>
                    ) : (
                        <p>üîí Veuillez vous connecter pour passer la commande.</p>
                    )}
                </>
            )}
        </div>
    );
};

export default CartPage;
