import React, { useState, useContext, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { CartContext } from "../context/CartContext";
import { UserContext } from "../context/UserContext";
import { PayPalScriptProvider, PayPalButtons } from "@paypal/react-paypal-js";
import "../styles/OrderPage.css";

const OrderPage = () => {
    const { cart, clearCart } = useContext(CartContext);
    const { user } = useContext(UserContext);
    const [address, setAddress] = useState(null);
    const [firstName, setFirstName] = useState("");
    const [lastName, setLastName] = useState("");
    const [phone, setPhone] = useState("");
    const [addressText, setAddressText] = useState("");
    const [extraInfo, setExtraInfo] = useState("");
    const [postalCode, setPostalCode] = useState("");
    const [defaultAddress, setDefaultAddress] = useState(false);
    const [isAddressSaved, setIsAddressSaved] = useState(false);
    const [isEditing, setIsEditing] = useState(false);  // ‚úÖ Nouveau : √©tat pour l'√©dition
    const [showPayment, setShowPayment] = useState(false);
    const [paymentMethod, setPaymentMethod] = useState("");  
    const navigate = useNavigate();

    const totalAmount = cart.reduce((acc, item) => acc + item.prix * item.quantity, 0);

    useEffect(() => {
        if (user) {
            axios.get(`http://localhost:5001/api/addresses/${user.id}`)
                .then((response) => {
                    if (response.data) {
                        setAddress(response.data);
                        setIsAddressSaved(true); 
                        setFirstName(response.data.first_name);
                        setLastName(response.data.last_name);
                        setPhone(response.data.phone);
                        setAddressText(response.data.address);
                        setPostalCode(response.data.postal_code);
                    }
                })
                .catch((error) => console.error("Erreur chargement de l'adresse :", error));
        }
    }, [user]);

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const data = {
                user_id: user.id,
                first_name: firstName,
                last_name: lastName,
                phone: phone,
                address: addressText,
                extra_info: extraInfo,
                postal_code: postalCode,
                is_default: defaultAddress ? 1 : 0,
            };
            await axios.post("http://localhost:5001/api/addresses/save", data, {
                headers: {
                    "Content-Type": "application/json"
                },
                withCredentials: true
            });

            alert("Adresse enregistr√©e !");
            setIsAddressSaved(true); 
            setIsEditing(false);  // ‚úÖ Sortir du mode √©dition apr√®s l'enregistrement
            setShowPayment(false);
        } catch (error) {
            console.error("‚ùå Erreur lors de l'enregistrement :", error);
            alert("Erreur lors de l'enregistrement de l'adresse");
        }
    };

    const handleUpdate = async (e) => {
        e.preventDefault();
        try {
            const data = {
                first_name: firstName,
                last_name: lastName,
                phone: phone,
                address: addressText,
                extra_info: extraInfo,
                postal_code: postalCode,
                is_default: defaultAddress ? 1 : 0,
            };
            await axios.put(`http://localhost:5001/api/addresses/update/${user.id}`, data, {
                headers: {
                    "Content-Type": "application/json"
                },
                withCredentials: true
            });
            alert("Adresse mise √† jour avec succ√®s !");
            setIsEditing(false); 
            setIsAddressSaved(true); 
        } catch (error) {
            console.error("‚ùå Erreur lors de la mise √† jour de l'adresse :", error);
            alert("Erreur lors de la mise √† jour de l'adresse");
        }
    };
    

    const handlePaymentMethod = (method) => {
        setPaymentMethod(method);
        setShowPayment(true);
    };

    return (
        <div className="order-page">
            <h2>Passer √† la caisse üá®üá¶</h2>
            <div className="order-container">
                <div className="order-left">
                    {isAddressSaved && !isEditing ? (
                        <>
                            <h3>Adresse de livraison enregistr√©e</h3>
                            <p><strong>Pr√©nom :</strong> {firstName}</p>
                            <p><strong>Nom de famille :</strong> {lastName}</p>
                            <p><strong>T√©l√©phone :</strong> {phone}</p>
                            <p><strong>Adresse :</strong> {addressText}</p>
                            <p><strong>Code postal :</strong> {postalCode}</p>
                            <button onClick={handleEdit} className="edit-btn">Modifier l'adresse</button>
                        </>
                    ) : (
                        <>
                            <h3>Adresse de livraison</h3>
                            <form onSubmit={handleSubmit}>
                                <div className="form-row">
                                    <input type="text" placeholder="Pr√©nom*" value={firstName} onChange={(e) => setFirstName(e.target.value)} required />
                                    <input type="text" placeholder="Nom de famille*" value={lastName} onChange={(e) => setLastName(e.target.value)} required />
                                </div>
                                <input type="tel" placeholder="T√©l√©phone*" value={phone} onChange={(e) => setPhone(e.target.value)} required />
                                <input type="text" placeholder="Adresse*" value={addressText} onChange={(e) => setAddressText(e.target.value)} required />
                                <input type="text" placeholder="Code postal*" value={postalCode} onChange={(e) => setPostalCode(e.target.value)} required />
                                <div className="checkbox-row">
                                    <input type="checkbox" checked={defaultAddress} onChange={(e) => setDefaultAddress(e.target.checked)} />
                                    <label>D√©finir comme l'adresse d'exp√©dition par d√©faut</label>
                                </div>
                                <button type="submit" className="save-btn">Enregistrer et continuer</button>
                            </form>
                        </>
                    )}
                </div>

                {isAddressSaved && (
                    <div className="order-right">
                        <h3>Total de la commande</h3>
                        <p>Sous-total : {totalAmount.toFixed(2)} CAD</p>
                        <p>Livraison : GRATUIT</p>
                        <p>Taxes : {(totalAmount * 0.15).toFixed(2)} CAD</p>
                        <p>Total estim√© : {(totalAmount * 1.15).toFixed(2)} CAD</p>
                        <h4>Choisissez votre m√©thode de paiement :</h4>
                        <button onClick={() => handlePaymentMethod("paypal")}>PayPal</button>
                        <button onClick={() => handlePaymentMethod("stripe")}>Stripe</button>
                    </div>
                )}
            </div>
        </div>
    );
};

export default OrderPage;
