import React, { useState, useContext } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { UserContext } from "../context/UserContext"; // ‚úÖ Import du contexte utilisateur
import "../styles/auth.css"; // Ajoute un fichier CSS personnalis√©

const LoginPage = () => {
  const [formData, setFormData] = useState({ email: "", mot_de_passe: "" });
  const [error, setError] = useState(null);
  const [errorMessage, setErrorMessage] = useState(""); // üî¥ Ajout d'un √©tat pour afficher un message d'erreur sp√©cifique
  const { login } = useContext(UserContext); // ‚úÖ Utilisation correcte du contexte utilisateur
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);
    setErrorMessage("");

    try {
      const response = await axios.post("http://localhost:5001/api/users/login", formData);
      
      if (response.data && response.data.user) {
        login(response.data.user); // ‚úÖ Met √† jour le contexte utilisateur
        localStorage.setItem("user", JSON.stringify(response.data.user));
        navigate("/");

        // üîÑ D√©clenche un √©v√©nement global pour forcer la mise √† jour instantan√©e du header
        window.dispatchEvent(new Event("storage"));
      } else {
        setError("Erreur de connexion, veuillez r√©essayer.");
      }
    } catch (error) {
      // üî¥ V√©rifie si l'erreur vient du blocage du compte (403 Forbidden)
      if (error.response && error.response.status === 403) {
        setErrorMessage("‚ö†Ô∏è Votre compte est bloqu√©. Contactez l'administration.");
      } else {
        setErrorMessage("‚ùå Email ou mot de passe incorrect.");
      }
      console.error("‚ùå Erreur lors de la connexion :", error);
    }
  };

  return (
    <div className="auth-container">
      <div className="auth-card">
        <div className="auth-left">
          <img src="/assets/login-bg.jpg" alt="Beauty" />
        </div>
        <div className="auth-right">
          <h1 className="brand-name">LYS√âA</h1>
          <h2>Connexion</h2>
          <form onSubmit={handleSubmit}>
            {errorMessage && <p className="error-message">{errorMessage}</p>} {/* ‚úÖ Message d'erreur affich√© ici */}
            <input type="email" name="email" placeholder="Email" onChange={handleChange} required />
            <input type="password" name="mot_de_passe" placeholder="Mot de passe" onChange={handleChange} required />
            <button type="submit">Se connecter</button>
            <p className="auth-note">
              En vous inscrivant, vous acc√©dez √† des offres exclusives et des conseils beaut√© personnalis√©s.
            </p>
          </form>
        </div>
      </div>
    </div>
  );
};

export default LoginPage;
