import React, { useState, useEffect, useContext } from "react";
import axios from "axios";
import ProductCard from "../components/ProductCard";
import "../styles/BeautyPage.css";
import { useNavigate } from "react-router-dom";
import { FavoritesContext } from "../context/FavoritesContext";
import { CartContext } from "../context/CartContext";

const BeautyPage = () => {
    const [products, setProducts] = useState([]);
    const [loading, setLoading] = useState(true);
    const [cart, setCart] = useState([]);
    const navigate = useNavigate();
    const { favorites, toggleFavorite } = useContext(FavoritesContext);

    useEffect(() => {
        axios
            .get("http://localhost:5001/api/products")
            .then((response) => {
                setProducts(response.data);
                setLoading(false);
            })
            .catch((error) => {
                console.error("‚ùå Erreur chargement des produits :", error.message);
                setLoading(false);
            });
    }, []);

    const addToCart = (product) => {
        if (!cart.some((p) => p.id === product.id)) {
            setCart([...cart, product]);
            console.log("üõí Produit ajout√© :", product);
        } else {
            console.log("‚ö†Ô∏è Produit d√©j√† dans le panier !");
        }
    };

    return (
        <div className="beauty-page">
            <h2 className="section-title">Nos Produits Beaut√©</h2>

            {loading ? (
                <p>Chargement des produits...</p>
            ) : products.length > 0 ? (
                <div className="product-grid">
                    {products.map((product) => (
                        <ProductCard 
                            key={product.id} 
                            product={product} 
                            addToCart={addToCart} 
                            toggleLike={() => toggleFavorite(product)} 
                            isLiked={favorites.some((fav) => fav.id === product.id)}
                            navigate={() => navigate(`/product/${product.id}`)}
                        />
                    ))}
                </div>
            ) : (
                <p className="no-products">Aucun produit disponible.</p>
            )}
        </div>
    );
};

export default BeautyPage;
