import React, { createContext, useState, useContext, useEffect } from "react";
import axios from "axios";
import { UserContext } from "./UserContext";

export const CartContext = createContext();

export const CartProvider = ({ children }) => {
    const { user } = useContext(UserContext);
    const [cart, setCart] = useState([]);

    // Charger le panier depuis la base de données
    useEffect(() => {
        if (user) {
            axios.get(`http://localhost:5001/api/cart/${user.id}`)
                .then((response) => setCart(response.data))
                .catch((error) => console.error("❌ Erreur chargement du panier :", error));
        }
    }, [user]);
    const express = require("express");
    const router = express.Router();
    const db = require("../config/db");
    
    // Ajouter un produit au panier
    router.post("/add", async (req, res) => {
        const { user_id, product_id, quantity } = req.body;
    
        try {
            // Vérifier si le produit existe déjà dans le panier
            const [existingProduct] = await db.promise().query(
                "SELECT * FROM cart WHERE user_id = ? AND product_id = ?",
                [user_id, product_id]
            );
    
            if (existingProduct.length > 0) {
                // Mettre à jour la quantité si le produit existe déjà
                await db.promise().query(
                    "UPDATE cart SET quantity = quantity + ? WHERE user_id = ? AND product_id = ?",
                    [quantity, user_id, product_id]
                );
            } else {
                // Ajouter le produit si ce n'est pas déjà dans le panier
                await db.promise().query(
                    "INSERT INTO cart (user_id, product_id, quantity) VALUES (?, ?, ?)",
                    [user_id, product_id, quantity]
                );
            }
    
            res.status(201).json({ message: "Produit ajouté au panier !" });
        } catch (error) {
            console.error("❌ Erreur ajout panier :", error.message);
            res.status(500).json({ error: "Erreur lors de l'ajout au panier" });
        }
    });
    
    
    // Récupérer le panier d'un utilisateur
    router.get("/:user_id", async (req, res) => {
        const { user_id } = req.params;
    
        try {
            const [cart] = await db.promise().query(
                `SELECT products.id, products.nom, products.prix, products.image_url, cart.quantity 
                 FROM cart 
                 JOIN products ON cart.product_id = products.id 
                 WHERE cart.user_id = ?`,
                [user_id]
            );
    
            res.json(cart);
        } catch (error) {
            console.error("❌ Erreur récupération panier :", error.message);
            res.status(500).json({ error: "Erreur lors de la récupération du panier" });
        }
    });
    
    // Vider le panier
    router.delete("/clear/:user_id", async (req, res) => {
        const { user_id } = req.params;
    
        try {
            await db.promise().query("DELETE FROM cart WHERE user_id = ?", [user_id]);
            res.status(200).json({ message: "Panier vidé avec succès !" });
        } catch (error) {
            console.error("❌ Erreur vidage panier :", error.message);
            res.status(500).json({ error: "Erreur lors du vidage du panier" });
        }
    });
    

    // Ajouter un produit au panier
    const addToCart = async (product) => {
        try {
            await axios.post("http://localhost:5001/api/cart/add", {
                user_id: user.id,
                product_id: product.id,
                quantity: 1,
            });
            setCart((prevCart) => [...prevCart, { ...product, quantity: 1 }]);
        } catch (error) {
            console.error("❌ Erreur ajout panier :", error.message);
        }
    };

    // Vider le panier
    const clearCart = async () => {
        setCart([]);
        try {
            await axios.delete(`http://localhost:5001/api/cart/clear/${user.id}`);
        } catch (error) {
            console.error("❌ Erreur lors du vidage du panier :", error);
        }
    };

    // Retirer un produit du panier
const removeFromCart = async (productId) => {
    try {
        await axios.delete(`http://localhost:5001/api/cart/remove/${productId}`, {
            data: { user_id: user.id }  // Passer l'ID de l'utilisateur dans le corps
        });
        setCart((prevCart) => prevCart.filter((item) => item.id !== productId));
    } catch (error) {
        console.error("❌ Erreur lors de la suppression du produit du panier :", error.message);
    }
};

    // Diminuer la quantité d'un produit
    const decreaseQuantity = async (productId) => {
        try {
            await axios.post("http://localhost:5001/api/cart/decrease", {
                user_id: user.id,
                product_id: productId,
            });
            setCart((prevCart) =>
                prevCart.map((item) =>
                    item.id === productId && item.quantity > 1
                        ? { ...item, quantity: item.quantity - 1 }
                        : item
                )
            );
        } catch (error) {
            console.error("❌ Erreur lors de la décrémentation :", error);
        }
    };

    return (
        <CartContext.Provider value={{ cart, addToCart, removeFromCart, clearCart, decreaseQuantity }}>
            {children}
        </CartContext.Provider>
    );
};
