import React, { createContext, useState, useEffect, useContext } from "react";
import axios from "axios";
import { UserContext } from "./UserContext"; // âœ… VÃ©rifie ce chemin

export const CartContext = createContext();

export const CartProvider = ({ children }) => {
    const { user } = useContext(UserContext);
    const [cart, setCart] = useState([]);

    // âœ… Charger le panier depuis la base de donnÃ©es lorsque l'utilisateur est connectÃ©
    useEffect(() => {
        if (user) {
            console.log("ðŸ“¥ Chargement du panier pour l'utilisateur", user.id);
            axios.get(`http://localhost:5000/api/cart/${user.id}`)
                .then(response => {
                    console.log("ðŸ“¦ Panier chargÃ© :", response.data);
                    setCart(response.data);
                })
                .catch(error => console.error("âŒ Erreur chargement panier :", error));
        }
    }, [user]);
    
    
    // âœ… Ajouter un produit au panier
    const addToCart = (product) => {
        if (!user) {
            alert("Veuillez vous connecter pour ajouter des produits au panier.");
            return;
        }
    
        console.log("ðŸ“© Envoi de la requÃªte au backend:", {
            userId: user.id,
            productId: product.id,
            quantity: 1
        });
    
        axios.post("http://localhost:5000/api/cart/add", {
            userId: user.id,
            productId: product.id,
            quantity: 1
        })
        .then(() => {
            console.log("âœ… Produit ajoutÃ© avec succÃ¨s !");
            setCart(prevCart => {
                const existingProduct = prevCart.find(item => item.id === product.id);
                if (existingProduct) {
                    return prevCart.map(item =>
                        item.id === product.id ? { ...item, quantity: item.quantity + 1 } : item
                    );
                } else {
                    return [...prevCart, { ...product, quantity: 1 }];
                }
            });
        })
        .catch(error => console.error("âŒ Erreur lors de l'ajout au panier :", error));
    };
    
    

    // âœ… Supprimer un produit du panier
    const removeFromCart = (productId) => {
        if (!user) return;

        axios.delete(`http://localhost:5000/api/cart/remove/${user.id}/${productId}`)
            .then(() => {
                setCart(prevCart => prevCart.filter(item => item.id !== productId));
            }).catch(error => console.error("âŒ Erreur suppression produit :", error));
    };

    // âœ… Vider le panier aprÃ¨s dÃ©connexion ou commande validÃ©e
    const clearCart = () => {
        setCart([]);
    };

    // âœ… Passer la commande et stocker dans `orders`
    const checkout = async () => {
        if (!user) {
            alert("Veuillez vous connecter pour passer une commande.");
            return;
        }

        try {
            const response = await axios.post("http://localhost:5000/api/orders/create", {
                userId: user.id,
                products: cart.map(({ id, quantity, prix }) => ({
                    productId: id,
                    quantity,
                    price: prix
                })),
                totalPrice: cart.reduce((acc, product) => acc + product.prix * product.quantity, 0)
            });

            if (response.data.success) {
                alert("âœ… Commande validÃ©e !");
                clearCart();
            } else {
                alert("âŒ Erreur lors de la validation de la commande.");
            }
        } catch (error) {
            console.error("âŒ Erreur checkout :", error);
        }
    };
    const decreaseQuantity = (productId) => {
        setCart((prevCart) =>
            prevCart.map((item) =>
                item.id === productId
                    ? { ...item, quantity: item.quantity > 1 ? item.quantity - 1 : 1 }
                    : item
            )
        );
    };
    
     
    

    return (
        <CartContext.Provider value={{ cart, addToCart, removeFromCart, clearCart,decreaseQuantity, checkout }}>
            {children}
        </CartContext.Provider>
    );
};
