import React, { useState } from "react";

export default function NewsletterForm() {
  const [email, setEmail] = useState("");
  const [loading, setLoading] = useState(false);
  const [msg, setMsg] = useState(null); // { type: 'success' | 'error', text: string }

  const handleNewsletterSubmit = async (e) => {
    e.preventDefault();
    if (!email) return;

    setLoading(true);
    setMsg(null);

    try {
      // ðŸ‘‰ Remplace l'URL par ton endpoint rÃ©el cÃ´tÃ© serveur
      // change juste cette ligne:
const res = await fetch("http://localhost:5001/api/newsletter/subscribe", {
  method: "POST",
  headers: { "Content-Type": "application/json" },
  body: JSON.stringify({ email }),
});


      if (!res.ok) throw new Error("Ã‰chec d'inscription");

      setMsg({ type: "success", text: "Merci ! Vous Ãªtes inscrit(e) ðŸŽ‰" });
      setEmail("");
    } catch (err) {
      console.error(err);
      setMsg({ type: "error", text: "Oups, rÃ©essayez dans un instant." });
    } finally {
      setLoading(false);
    }
  };

  return (
    <form onSubmit={handleNewsletterSubmit} className="newsletter-form">
      <input
        type="email"
        placeholder="Votre e-mail"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        required
        aria-label="Adresse e-mail"
      />
      <button type="submit" disabled={loading || !email}>
        {loading ? "Envoi..." : "Rejoindre"}
      </button>

      {msg && (
        <p
          style={{
            color: msg.type === "success" ? "green" : "red",
            marginTop: 6,
          }}
        >
          {msg.text}
        </p>
      )}
    </form>
  );
}
