import React, { useContext } from "react";
import { useNavigate } from "react-router-dom";
import { FaHeart, FaShoppingCart } from "react-icons/fa"; // ‚úÖ Ic√¥nes
import "../styles/ProductCard.css";
import { FavoritesContext } from "../context/FavoritesContext"; // ‚úÖ Import du contexte favoris
import { CartContext } from "../context/CartContext"; // ‚úÖ Import du panier

const ProductCard = ({ product }) => {
    const navigate = useNavigate();
    const { favorites, toggleFavorite } = useContext(FavoritesContext); 
    const { addToCart } = useContext(CartContext); 
    const isFavorite = favorites.some((fav) => fav.id === product.id);

    // üîç V√©rifier l'URL de l'image
    console.log("URL de l'image :", `http://localhost:5001${product.image_url}`);

    return (
        <div className="product-card">
            {/* ‚ù§Ô∏è Ic√¥ne Favoris en haut √† droite */}
            <FaHeart 
                className={`like-icon ${isFavorite ? "liked" : ""}`} 
                onClick={() => {
                    toggleFavorite(product);
                    console.log("Favoris mis √† jour:", favorites);
                }} 
            />

            {/* üñºÔ∏è Image Produit */}
            <img src={`http://localhost:5001${product.image_url}`} alt={product.nom} className="product-img" />
            
            {/* üìå Infos Produit */}
            <div className="product-info">
                <h3>{product.nom}</h3>
                <p className="product-price"><strong>{product.prix} CAD</strong></p>
                <button onClick={() => toggleFavorite(product)} className="favorite-btn">
                    {isFavorite ? "üíñ Retirer des Favoris" : "ü§ç Ajouter aux Favoris"}
                </button>
                {/* üìå Boutons */}
                <div className="product-buttons">
                <FaHeart 
    className={`like-icon ${isFavorite ? "liked" : ""}`} 
    onClick={() => {
        toggleFavorite(product);
        console.log("Favoris mis √† jour:", favorites);
    }} 
/>

                    <button className="view-more" onClick={() => navigate(`/product/${product.id}`)}>
                        Voir plus
                    </button>
                </div>
            </div>
        </div>
    );
};


export default ProductCard;
