import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate, useParams } from "react-router-dom";
import "../styles/EditProductPage.css";

const brands = ["Karine Joncas", "Watier", "Reversa"];
const EditProductPage = () => {
  const { id } = useParams(); // R√©cup√©rer l'ID depuis l'URL
  const navigate = useNavigate();

  // √âtat du formulaire
  const [formData, setFormData] = useState({
    nom: "",
    prix: "",
    description: "",
    image: null,
    brand: "Autre",
  });

  const [previewImage, setPreviewImage] = useState(null); // Image actuelle

  // ‚úÖ Charger les infos du produit existant
  useEffect(() => {
    axios
      .get(`http://localhost:5001/api/products/${id}`)
      .then((response) => {
        const product = response.data;
        setFormData({
          nom: product.nom,
          prix: product.prix,
          description: product.description || "",
          brand: product.brand || "",
          image: null,
        });
        setPreviewImage(`http://localhost:5000${product.image_url}`);
      })
      .catch((error) => console.error("‚ùå Erreur chargement du produit :", error));
  }, [id]);
  <select name="brand" value={formData.brand} onChange={handleChange} required>
    <option value="">S√©lectionner une marque</option>
    <option value="Karine Joncas">Karine Joncas</option>
    <option value="Watier">Watier</option>
    <option value="Reversa">Reversa</option>
</select>

  // ‚úÖ Supprimer le produit
  const deleteProduct = async () => {
    if (!window.confirm("√ätes-vous s√ªr de vouloir supprimer ce produit ?")) {
      return;
    }

    try {
      const response = await axios.delete(`http://localhost:5000/api/products/${id}`);

      if (response.status === 200) {
        alert("‚úÖ Produit supprim√© !");
        navigate("/admin/products"); // ‚úÖ Rediriger apr√®s suppression
      } else {
        alert(`‚ùå Erreur : ${response.data.message}`);
      }
    } catch (error) {
      console.error("‚ùå Erreur lors de la suppression :", error);
      alert("‚ùå Erreur lors de la suppression du produit !");
    }
  };

  // ‚úÖ G√©rer les changements dans les champs
  const handleChange = (e) => {
    if (e.target.name === "image") {
      const file = e.target.files[0];
      setFormData({ ...formData, image: file });
      setPreviewImage(URL.createObjectURL(file)); // Affiche l'image s√©lectionn√©e
    } else {
      setFormData({ ...formData, [e.target.name]: e.target.value });
    }
  };

  const handleDelete = (id) => {
    if (window.confirm("Voulez-vous vraiment supprimer ce produit ?")) {
        axios.delete(`http://localhost:5000/api/products/${id}`)
            .then(() => {
                setProducts(prevProducts => prevProducts.filter(product => product.id !== id));
                alert("‚úÖ Produit supprim√© !");
                navigate("/admin/products"); // Rediriger apr√®s suppression
            })
            .catch(error => {
                console.error("‚ùå Erreur lors de la suppression :", error.response ? error.response.data : error);
                alert(`‚ùå Erreur lors de la suppression : ${error.response ? error.response.data.message : "Probl√®me inconnu"}`);
            });
    }
};

const handleSubmit = async (e) => {
    e.preventDefault();
    const data = new FormData();
    data.append("nom", formData.nom);
    data.append("prix", formData.prix);
    data.append("description", formData.description);
    data.append("brand", formData.brand); // ‚úÖ Assure-toi que c'est bien `brand`
    if (formData.image) {
        data.append("image", formData.image);
    }

    try {
        await axios.put(`http://localhost:5000/api/products/${id}`, data, {
            headers: { "Content-Type": "multipart/form-data" },
        });

        alert("‚úÖ Produit mis √† jour avec succ√®s !");
        navigate("/admin/products");
    } catch (error) {
        console.error("‚ùå Erreur mise √† jour du produit :", error);
        alert("‚ùå Erreur lors de la mise √† jour !");
    }
};


  return (
    <div className="edit-product-page">
      <div className="product-form-container">
        <div className="product-left">
          {previewImage && <img src={previewImage} alt="Produit actuel" />}
        </div>
        <div className="product-right">
          <h2>Modifier le Produit</h2>
          <form onSubmit={handleSubmit} encType="multipart/form-data">
            <input
              type="text"
              name="nom"
              value={formData.nom}
              onChange={handleChange}
              placeholder="Nom du produit"
              required
            />
            <input
              type="text"
              name="prix"
              value={formData.prix}
              onChange={handleChange}
              placeholder="Prix"
              required
            />
            <textarea
              name="description"
              value={formData.description}
              onChange={handleChange}
              placeholder="Description du produit"
              rows="4"
              required
            />
            <input type="file" name="image" accept="image/*" onChange={handleChange} />
            <button type="submit">Mettre √† jour</button>
          </form>
          <button className="delete-btn" onClick={deleteProduct}>
            üóëÔ∏è Supprimer le produit
          </button>
        </div>
      </div>
    </div>
  );
};

export default EditProductPage;
