import React, { useState, useContext } from "react";
import { useNavigate } from "react-router-dom";
import { CartContext } from "../context/CartContext";
import { PayPalButton } from "@paypal/react-paypal-js";
import "../styles/OrderPage.css";

const OrderPage = () => {
    const { cart, clearCart } = useContext(CartContext);
    const [name, setName] = useState("");
    const [address, setAddress] = useState("");
    const [phone, setPhone] = useState("");
    const navigate = useNavigate();

    const totalAmount = cart.reduce((acc, item) => acc + item.prix * item.quantity, 0);

    const handlePaymentSuccess = (details) => {
        alert("Paiement réussi !");
        clearCart();
        navigate("/");
    };

    return (
        <div className="order-page">
            <h2>Passer la commande</h2>
            <form className="order-form">
                <label>Nom complet</label>
                <input
                    type="text"
                    placeholder="Votre nom"
                    value={name}
                    onChange={(e) => setName(e.target.value)}
                    required
                />

                <label>Adresse de livraison</label>
                <input
                    type="text"
                    placeholder="Votre adresse"
                    value={address}
                    onChange={(e) => setAddress(e.target.value)}
                    required
                />

                <label>Téléphone</label>
                <input
                    type="tel"
                    placeholder="Votre numéro"
                    value={phone}
                    onChange={(e) => setPhone(e.target.value)}
                    required
                />

                <h3>Total : {totalAmount.toFixed(2)} CAD</h3>

                <PayPalButton
                    amount={totalAmount.toFixed(2)}
                    onSuccess={handlePaymentSuccess}
                    options={{
                        clientId: "VOTRE_CLIENT_ID_PAYPAL",
                    }}
                />
            </form>
        </div>
    );
};

export default OrderPage;
