import React, { useState, useContext } from "react";
import { useNavigate } from "react-router-dom";
import { CartContext } from "../context/CartContext";
import { PayPalScriptProvider, PayPalButtons } from "@paypal/react-paypal-js";
import "../styles/OrderPage.css";

const OrderPage = () => {
    const { cart, clearCart } = useContext(CartContext);
    const [name, setName] = useState("");
    const [address, setAddress] = useState("");
    const [phone, setPhone] = useState("");
    const [showPayment, setShowPayment] = useState(false); // ✅ Gérer l'affichage du paiement
    const navigate = useNavigate();

    const totalAmount = cart.reduce((acc, item) => acc + item.prix * item.quantity, 0);

    const handlePaymentSuccess = (details) => {
        alert("Paiement réussi !");
        clearCart();
        navigate("/");
    };

    // ✅ Fonction pour enregistrer les données et afficher le paiement
    const handleSubmit = (e) => {
        e.preventDefault();
        if (name && address && phone) {
            setShowPayment(true);
            alert("Informations enregistrées avec succès !");
        } else {
            alert("Veuillez remplir tous les champs !");
        }
    };

    return (
        <div className="order-page">
            <h2>Passer la commande</h2>
            <form className="order-form" onSubmit={handleSubmit}>
                <label>Nom complet</label>
                <input
                    type="text"
                    placeholder="Votre nom"
                    value={name}
                    onChange={(e) => setName(e.target.value)}
                    required
                />

                <label>Adresse de livraison</label>
                <input
                    type="text"
                    placeholder="Votre adresse"
                    value={address}
                    onChange={(e) => setAddress(e.target.value)}
                    required
                />

                <label>Téléphone</label>
                <input
                    type="tel"
                    placeholder="Votre numéro"
                    value={phone}
                    onChange={(e) => setPhone(e.target.value)}
                    required
                />

                <h3>Total : {totalAmount.toFixed(2)} CAD</h3>

                {/* ✅ Bouton pour enregistrer les informations */}
                {!showPayment && (
                    <button type="submit" className="save-btn">
                        Enregistrer et Payer
                    </button>
                )}
            </form>

            {/* ✅ Affichage du bouton PayPal après enregistrement */}
            {showPayment && (
                <PayPalScriptProvider options={{ "client-id": "VOTRE_CLIENT_ID_PAYPAL" }}>
                    <PayPalButtons
                        style={{ layout: "vertical" }}
                        createOrder={(data, actions) => {
                            return actions.order.create({
                                purchase_units: [{
                                    amount: {
                                        value: totalAmount.toFixed(2),
                                        currency_code: "CAD"
                                    }
                                }]
                            });
                        }}
                        onApprove={(data, actions) => {
                            return actions.order.capture().then((details) => {
                                handlePaymentSuccess(details);
                            });
                        }}
                        onError={(err) => {
                            console.error("Erreur de paiement :", err);
                            alert("Le paiement a échoué, veuillez réessayer.");
                        }}
                    />
                </PayPalScriptProvider>
            )}
        </div>
    );
};

export default OrderPage;
