import React, { useState, useEffect, useContext } from "react";
import { Link, useNavigate } from "react-router-dom";
import { FaUserCircle, FaHeart, FaShoppingCart } from "react-icons/fa";
import "../styles/Navbar.css"; // ‚úÖ Style du Navbar
import { UserContext } from "../context/UserContext"; // ‚úÖ Contexte utilisateur
import { FavoritesContext } from "../context/FavoritesContext"; // ‚úÖ Contexte favoris
import { CartContext } from "../context/CartContext"; // ‚úÖ Import du contexte du panier

const Navbar = () => {
  const { user, logout } = useContext(UserContext); 
  const { favorites } = useContext(FavoritesContext); // ‚úÖ Acc√©der aux favoris
  const { cart } = useContext(CartContext); // ‚úÖ Acc√©der au panier
  const navigate = useNavigate();
  const [menuOpen, setMenuOpen] = useState(false);

  useEffect(() => {
    const checkUser = () => {
      const storedUser = localStorage.getItem("user");
      if (!storedUser) {
        logout();
      }
    };

    checkUser();
    window.addEventListener("storage", checkUser);
    return () => window.removeEventListener("storage", checkUser);
  }, [logout]);

  const handleLogout = () => {
    logout();
    setMenuOpen(false);
    navigate("/");
  };
  
  const handleSearch = (e) => {
    setSearchTerm(e.target.value);
    onSearch(e.target.value); // Transmettre la valeur √† la fonction parent
  };


  return (
    <nav className="navbar">
      <Link to="/" className="logo">Lys√©a </Link>

      <div className="nav-links">
        <Link to="/BeautyPage">BEAUT√â</Link>
        <Link to="/lifestyle">LIFESTYLE</Link>
        <Link to="/blog">BLOG</Link>
      </div>

      <div className="nav-icons">
        {/* ‚ù§Ô∏è Ic√¥ne Favoris avec le badge */}
        <Link to="/favorites" className="fav-link">
          <FaHeart className="icon" />
          {favorites.length > 0 && <span className="fav-badge">{favorites.length}</span>}
        </Link>

        {/* üõí Ic√¥ne Panier avec badge */}
        <Link to="/cart" className="cart-link">
          <FaShoppingCart className="icon" />
          {cart.length > 0 && <span className="cart-badge">{cart.length}</span>}
        </Link>

        {user && <span className="welcome-text">Bonjour, {user.nom}</span>}
        {user && user.role === "admin" && <Link to="/admin" className="admin-link">Admin</Link>}

        {/* üë§ Ic√¥ne Utilisateur */}
        <div className="user-menu">
          <FaUserCircle className="icon" onClick={() => setMenuOpen(!menuOpen)} />
          {menuOpen && (
            <div className="dropdown">
              {user ? (
                <button onClick={handleLogout} className="logout-btn">D√©connecter</button>
              ) : (
                <>
                  <Link to="/login" className="dropdown-item">Sign In</Link>
                  <Link to="/register" className="dropdown-item">Register Now</Link>
                </>
              )}
            </div>
          )}
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
