import React, { useState, useEffect, useContext } from "react";
import { Link, useNavigate } from "react-router-dom";
import { FaUserCircle, FaHeart, FaShoppingCart } from "react-icons/fa";
import "../styles/Navbar.css";
import { UserContext } from "../context/UserContext";
import { FavoritesContext } from "../context/FavoritesContext";
import { CartContext } from "../context/CartContext";

const Navbar = ({ onSearch }) => {
  const { user, logout } = useContext(UserContext);
  const { favorites } = useContext(FavoritesContext);
  const { cart } = useContext(CartContext);
  const navigate = useNavigate();
  const [menuOpen, setMenuOpen] = useState(false);
  const [searchTerm, setSearchTerm] = useState("");

  const handleSearch = (e) => {
    setSearchTerm(e.target.value);
    onSearch(e.target.value); // Transmettre la valeur Ã  la fonction parent
  };
  const handleLogout = () => {
    clearCart(); // âœ… Vider le panier lors de la dÃ©connexion
    logout();
    navigate("/login");
};


  return (
    <nav className="navbar">
      <Link to="/" className="logo">LysÃ©a</Link>

      <div className="nav-links">
        <Link to="/BeautyPage">BEAUTÃ‰</Link>
        <Link to="/lifestyle">LIFESTYLE</Link>
        <Link to="/blog">BLOG</Link>
      </div>

      {/* ðŸŒŸ Barre de recherche */}
      <div className="search-bar">
        <input
          type="text"
          placeholder="Rechercher..."
          value={searchTerm}
          onChange={handleSearch}
        />
      </div>

      <div className="nav-icons">
        <Link to="/favorites" className="fav-link">
          <FaHeart className="icon" />
          {favorites.length > 0 && <span className="fav-badge">{favorites.length}</span>}
        </Link>

        <Link to="/cart" className="cart-link">
          <FaShoppingCart className="icon" />
          {cart.length > 0 && <span className="cart-badge">{cart.length}</span>}
        </Link>

        {user && <span className="welcome-text">Bonjour, {user.nom}</span>}
        {user && user.role === "admin" && <Link to="/admin" className="admin-link">Admin</Link>}

        <div className="user-menu">
          <FaUserCircle className="icon" onClick={() => setMenuOpen(!menuOpen)} />
          {menuOpen && (
            <div className="dropdown">
              {user ? (
                <button onClick={logout} className="logout-btn">DÃ©connecter</button>
              ) : (
                <>
                  <Link to="/login" className="dropdown-item">Sign In</Link>
                  <Link to="/register" className="dropdown-item">Register Now</Link>
                </>
              )}
            </div>
          )}
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
