import "../styles/AdminPage.css"; // âœ… Assure-toi que ce fichier existe

const AdminPage = () => {
  const [product, setProduct] = useState({ nom: "", prix: "", image: "" });

  const handleChange = (e) => {
    setProduct({ ...product, [e.target.name]: e.target.value });
  };

  const handleAddProduct = () => {
    alert(`Produit ajoutÃ©: ${product.nom}, Prix: ${product.prix}, Image: ${product.image}`);
  };

  return (
    <div className="admin-container">
      <h1>Page d'Administration</h1>

      {/* Section Ajout Produit */}
      <div className="product-form">
        <input type="text" name="nom" placeholder="Nom du produit" onChange={handleChange} />
        <input type="text" name="prix" placeholder="Prix" onChange={handleChange} />
        <input type="text" name="image" placeholder="URL de l'image" onChange={handleChange} />
        <button className="add-btn" onClick={handleAddProduct}>Ajouter</button>
      </div>

      {/* Boutons pour les autres pages */}
      <div className="admin-links">
        <Link to="/admin/users" className="admin-button">ðŸ‘¤ Liste des Utilisateurs</Link>
        <Link to="/admin/products" className="admin-button">ðŸ“¦ Liste des Produits</Link>
        <Link to="/admin/orders" className="admin-button">ðŸ“œ Liste des Commandes</Link>
        <Link to="/admin/add-product" className="admin-button add-product">âž• Ajouter un Produit</Link>
      </div>
    </div>
  );
};

export default AdminPage;
