import React, { useState, useContext } from "react";
import { useNavigate } from "react-router-dom";
import { CartContext } from "../context/CartContext";
import { PayPalScriptProvider, PayPalButtons } from "@paypal/react-paypal-js";
import "../styles/OrderPage.css";

const OrderPage = () => {
    const { cart, clearCart } = useContext(CartContext);
    const [firstName, setFirstName] = useState("");
    const [lastName, setLastName] = useState("");
    const [phone, setPhone] = useState("");
    const [address, setAddress] = useState("");
    const [extraInfo, setExtraInfo] = useState("");
    const [postalCode, setPostalCode] = useState("");
    const [defaultAddress, setDefaultAddress] = useState(false);
    const [showPayment, setShowPayment] = useState(false);
    const navigate = useNavigate();

    const totalAmount = cart.reduce((acc, item) => acc + item.prix * item.quantity, 0);

    const handlePaymentSuccess = (details) => {
        alert("Paiement r√©ussi !");
        clearCart();
        navigate("/");
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
    
        if (!user) {
            alert("Veuillez vous connecter !");
            return;
        }
    
        try {
            await axios.post("http://localhost:5001/api/addresses/save", {
                user_id: user.id,
                first_name: firstName,
                last_name: lastName,
                phone,
                address,
                extra_info: extraInfo,
                postal_code: postalCode,
                is_default: defaultAddress,
            });
    
            alert("Adresse enregistr√©e avec succ√®s !");
            navigate("/cart");
        } catch (error) {
            console.error("‚ùå Erreur lors de l'enregistrement de l'adresse :", error);
            alert("Erreur lors de l'enregistrement de l'adresse.");
        }
    };
    

    return (
        <div className="order-page">
            <h2>Adresse de livraison</h2>
            <form className="order-form" onSubmit={handleSubmit}>
                <label>Exp√©dier √† Canada üá®üá¶</label>

                <div className="form-row">
                    <input
                        type="text"
                        placeholder="Pr√©nom*"
                        value={firstName}
                        onChange={(e) => setFirstName(e.target.value)}
                        required
                    />
                    <input
                        type="text"
                        placeholder="Nom de famille*"
                        value={lastName}
                        onChange={(e) => setLastName(e.target.value)}
                        required
                    />
                </div>

                <input
                    type="tel"
                    placeholder="T√©l√©phone*"
                    value={phone}
                    onChange={(e) => setPhone(e.target.value)}
                    required
                />

                <input
                    type="text"
                    placeholder="Adresse*"
                    value={address}
                    onChange={(e) => setAddress(e.target.value)}
                    required
                />

                <input
                    type="text"
                    placeholder="Avertisseur de porte, code du b√¢timent..."
                    value={extraInfo}
                    onChange={(e) => setExtraInfo(e.target.value)}
                />

                <div className="form-row">
                    <input
                        type="text"
                        placeholder="Code postal*"
                        value={postalCode}
                        onChange={(e) => setPostalCode(e.target.value)}
                        required
                    />
                    <span>Saisir un code postal pour voir la ville et l'√âtat/la r√©gion.</span>
                </div>

                <div className="checkbox-row">
                    <input
                        type="checkbox"
                        checked={defaultAddress}
                        onChange={(e) => setDefaultAddress(e.target.checked)}
                    />
                    <label>D√©finir comme l'adresse d'exp√©dition par d√©faut</label>
                </div>

                <button type="submit" className="save-btn">
                    Enregistrer et continuer
                </button>
            </form>

            {showPayment && (
                <PayPalScriptProvider options={{ "client-id": "VOTRE_CLIENT_ID_PAYPAL" }}>
                    <PayPalButtons
                        style={{ layout: "vertical" }}
                        createOrder={(data, actions) => {
                            return actions.order.create({
                                purchase_units: [{
                                    amount: {
                                        value: totalAmount.toFixed(2),
                                        currency_code: "CAD"
                                    }
                                }]
                            });
                        }}
                        onApprove={(data, actions) => {
                            return actions.order.capture().then((details) => {
                                handlePaymentSuccess(details);
                            });
                        }}
                        onError={(err) => {
                            console.error("Erreur de paiement :", err);
                            alert("Le paiement a √©chou√©, veuillez r√©essayer.");
                        }}
                    />
                </PayPalScriptProvider>
            )}
        </div>
    );
};

export default OrderPage;
