import React, { useState } from "react";

const PayPalButton = ({ amount }) => {
    const [loading, setLoading] = useState(false);

    const handlePayment = async () => {
        setLoading(true);
        try {
            const response = await fetch("http://localhost:5001/api/checkout/create-payment", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({ amount })
            });

            const data = await response.json();
            if (data.approvalUrl) {
                window.location.href = data.approvalUrl; // Redirige vers PayPal
            } else {
                alert("Erreur lors de la création du paiement avec PayPal");
            }
        } catch (error) {
            console.error("❌ Erreur PayPal :", error);
            alert("Erreur lors du paiement.");
        }
        setLoading(false);
    };

    return (
        <button onClick={handlePayment} className="btn-paypal" disabled={loading}>
            {loading ? "Chargement..." : "Payer avec PayPal"}
        </button>
    );
};

export default PayPalButton;
