import React, { useContext, useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { CartContext } from "../context/CartContext";
import { UserContext } from "../context/UserContext";
import { FaTrash } from "react-icons/fa";
import "../styles/CartPage.css";

const CartPage = () => {
    const { cart, addToCart, removeFromCart, clearCart, decreaseQuantity } = useContext(CartContext);
    const { user } = useContext(UserContext);
    const navigate = useNavigate();
    const [address, setAddress] = useState(null);

    // Charger l'adresse depuis la base de donn√©es
    useEffect(() => {
        if (user) {
            axios.get(`http://localhost:5001/api/addresses/${user.id}`)
                .then((response) => setAddress(response.data))
                .catch((error) => console.error("‚ùå Erreur chargement de l'adresse :", error));
        }
    }, [user]);
    useEffect(() => {
        console.log("üì¶ Produits dans le panier :", cart);
        cart.forEach((product) => {
            console.log(`Produit : ${product.nom}, Prix : ${product.prix}, Image : ${product.image_url}`);
        });
    }, [cart]);
    
    // Calculer le prix total du panier
    const totalPrice = cart.reduce((acc, product) => {
        return acc + (product.prix ? product.prix * product.quantity : 0);
    }, 0);

    return (
        <div className="cart-container">
            <h2>üõí Mon Panier</h2>

            {address ? (
                <div className="address-info">
                    <h3>Adresse de livraison</h3>
                    <p>{address.first_name} {address.last_name}</p>
                    <p>{address.phone}</p>
                    <p>{address.address}</p>
                    {address.extra_info && <p>{address.extra_info}</p>}
                    <p>{address.postal_code}</p>
                    <button onClick={() => navigate("/order")} className="edit-address-btn">
                        Modifier l'adresse
                    </button>
                </div>
            ) : (
                <p>Adresse non d√©finie</p>
            )}

            {cart.length === 0 ? (
                <p>Votre panier est vide.</p>
            ) : (
                <>
                    <ul className="cart-list">
                        {cart.map((product) => (
                            <li key={product.id} className="cart-item">
                                <div className="cart-item-details">
                                    {/* Image du produit */}
                                    {product.image_url && (
                                        <div className="cart-item-image">
                                            <img
                                                src={`http://localhost:5001${product.image_url}`}
                                                alt={product.nom}
                                            />
                                        </div>
                                    )}
                                    <div className="cart-item-info">
                                        <h3>{product.nom}</h3>
                                        <p>{product.prix ? `${product.prix.toFixed(2)} CAD` : "Prix non disponible"}</p>

                                        {/* Gestion de la quantit√© et suppression */}
                                        <div className="quantity-controls">
                                            <button onClick={() => decreaseQuantity(product.id)}>-</button>
                                            <span>{product.quantity}</span>
                                            <button onClick={() => addToCart(product)}>+</button>
                                        </div>

                                        {/* Calcul du total par produit */}
                                        <p className="cart-item-total">
                                            <strong>Total : {product.prix ? (product.prix * product.quantity).toFixed(2) : "0.00"} CAD</strong>
                                        </p>
                                    </div>

                                    {/* Bouton de suppression */}
                                    <button className="remove-btn" onClick={() => removeFromCart(product.id)}>
                                        <FaTrash /> Retirer
                                    </button>
                                </div>
                            </li>
                        ))}
                    </ul>

                    <div className="cart-summary">
                        <h3 className="total-price">üí∞ Total √† payer : {totalPrice.toFixed(2)} CAD</h3>

                        {/* Bouton pour vider le panier */}
                        <button className="clear-cart" onClick={clearCart}>üóëÔ∏è Vider le panier</button>

                        {user ? (
                            <button onClick={() => navigate("/order")} className="checkout-btn">
                                ‚úÖ Passer la commande
                            </button>
                        ) : (
                            <p>üîí Veuillez vous connecter pour passer la commande.</p>
                        )}
                    </div>
                </>
            )}
        </div>
    );
};

export default CartPage;
