import React, { useEffect, useContext } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import axios from "axios";
import { UserContext } from "../context/UserContext";
import { CartContext } from "../context/CartContext"; // üîÅ import du contexte du panier

const PaypalSuccess = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const { user } = useContext(UserContext);
  const { clearCart } = useContext(CartContext); // ‚úÖ r√©cup√©ration de la fonction clearCart

  useEffect(() => {
    const query = new URLSearchParams(location.search);
    const orderID = query.get("token"); // PayPal renvoie l‚ÄôorderID dans "token"

    const capturePayment = async () => {
      try {
        const response = await axios.post("http://localhost:5001/api/checkout/capture-payment", {
          orderID,
          userId: user?.id
        });

        if (response.status === 200) {
          clearCart(); // ‚úÖ vider le panier c√¥t√© frontend
          alert("‚úÖ Paiement confirm√© !");
          navigate("/");
        } else {
          alert("‚ùå Erreur de confirmation du paiement");
        }
      } catch (error) {
        console.error("‚ùå Erreur lors de la capture du paiement :", error);
        alert("Erreur lors de la finalisation du paiement.");
      }
    };

    if (orderID && user?.id) {
      capturePayment();
    }
  }, [location, navigate, user, clearCart]);

  return (
    <div className="success-container">
      <h2>Validation du paiement...</h2>
      <p>Merci de patienter pendant que nous confirmons votre paiement.</p>
    </div>
  );
};

export default PaypalSuccess;
