import React, { useState, useContext, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { CartContext } from "../context/CartContext";
import { UserContext } from "../context/UserContext";
import { PayPalScriptProvider, PayPalButtons } from "@paypal/react-paypal-js";
import { loadStripe } from "@stripe/stripe-js";
import { Elements, CardElement, useElements, useStripe } from "@stripe/react-stripe-js";
import "../styles/OrderPage.css";

const stripePromise = loadStripe("VOTRE_STRIPE_PUBLIC_KEY");

const OrderPage = () => {
    const { cart, clearCart } = useContext(CartContext);
    const { user } = useContext(UserContext);
    const [address, setAddress] = useState(null);
    const [firstName, setFirstName] = useState("");
    const [lastName, setLastName] = useState("");
    const [phone, setPhone] = useState("");
    const [addressText, setAddressText] = useState("");
    const [extraInfo, setExtraInfo] = useState("");
    const [postalCode, setPostalCode] = useState("");
    const [defaultAddress, setDefaultAddress] = useState(false);
    const [showPayment, setShowPayment] = useState(false);
    const [paymentMethod, setPaymentMethod] = useState("paypal"); // Choix de paiement
    const navigate = useNavigate();
    const stripe = useStripe();
    const elements = useElements();

    const totalAmount = cart.reduce((acc, item) => acc + item.prix * item.quantity, 0);

    useEffect(() => {
        if (user) {
            axios.get(`http://localhost:5001/api/addresses/${user.id}`)
                .then((response) => setAddress(response.data))
                .catch((error) => console.error("Erreur chargement de l'adresse :", error));
        }
    }, [user]);

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await axios.post("http://localhost:5001/api/addresses/save", {
                user_id: user.id,
                first_name: firstName,
                last_name: lastName,
                phone,
                address: addressText,
                extra_info: extraInfo,
                postal_code: postalCode,
                is_default: defaultAddress,
            });
            alert("Adresse enregistr√©e !");
            setShowPayment(true);  // ‚úÖ Afficher les options de paiement
        } catch (error) {
            console.error("Erreur lors de l'enregistrement :", error);
        }
    };

    const handlePaymentSuccess = () => {
        alert("Paiement r√©ussi !");
        clearCart();
        navigate("/confirmation", { state: { message: "Merci pour votre achat !" } });
    };

    const handleStripePayment = async (event) => {
        event.preventDefault();
        if (!stripe || !elements) return;

        const { error, paymentIntent } = await stripe.confirmPayment({
            elements,
            confirmParams: {
                return_url: "http://localhost:3000/confirmation",
            },
        });

        if (error) {
            console.error("Erreur de paiement Stripe :", error);
            alert("Le paiement a √©chou√©, veuillez r√©essayer.");
        } else if (paymentIntent) {
            handlePaymentSuccess();
        }
    };

    return (
        <div className="order-page">
            <h2>Passer √† la caisse üá®üá¶</h2>

            <div className="order-container">
                <div className="order-left">
                    <h3>Adresse de livraison</h3>
                    <form onSubmit={handleSubmit}>
                        <div className="form-row">
                            <input type="text" placeholder="Pr√©nom*" value={firstName} onChange={(e) => setFirstName(e.target.value)} required />
                            <input type="text" placeholder="Nom de famille*" value={lastName} onChange={(e) => setLastName(e.target.value)} required />
                        </div>
                        <input type="tel" placeholder="T√©l√©phone*" value={phone} onChange={(e) => setPhone(e.target.value)} required />
                        <input type="text" placeholder="Adresse*" value={addressText} onChange={(e) => setAddressText(e.target.value)} required />
                        <input type="text" placeholder="Code postal*" value={postalCode} onChange={(e) => setPostalCode(e.target.value)} required />
                        <div className="checkbox-row">
                            <input type="checkbox" checked={defaultAddress} onChange={(e) => setDefaultAddress(e.target.checked)} />
                            <label>D√©finir comme l'adresse d'exp√©dition par d√©faut</label>
                        </div>
                        <button type="submit" className="save-btn">Enregistrer et continuer</button>
                    </form>
                </div>

                <div className="order-right">
                    <h3>Total de la commande</h3>
                    <p>Sous-total : {totalAmount.toFixed(2)} CAD</p>
                    <p>Livraison : GRATUIT</p>
                    <p>Taxes : {(totalAmount * 0.15).toFixed(2)} CAD</p>
                    <p>Total estim√© : {(totalAmount * 1.15).toFixed(2)} CAD</p>

                    <label>M√©thode de paiement :</label>
                    <select onChange={(e) => setPaymentMethod(e.target.value)} value={paymentMethod}>
                        <option value="paypal">PayPal</option>
                        <option value="stripe">Carte de cr√©dit (Stripe)</option>
                    </select>

                    {showPayment && paymentMethod === "paypal" && (
                        <PayPalScriptProvider options={{ "client-id": "VOTRE_CLIENT_ID_PAYPAL" }}>
                            <PayPalButtons
                                style={{ layout: "vertical" }}
                                createOrder={(data, actions) => {
                                    return actions.order.create({
                                        purchase_units: [{ amount: { value: (totalAmount * 1.15).toFixed(2), currency_code: "CAD" } }]
                                    });
                                }}
                                onApprove={(data, actions) => {
                                    return actions.order.capture().then((details) => {
                                        handlePaymentSuccess();
                                    });
                                }}
                                onError={(err) => {
                                    console.error("Erreur de paiement :", err);
                                    alert("Le paiement a √©chou√©, veuillez r√©essayer.");
                                }}
                            />
                        </PayPalScriptProvider>
                    )}

                    {showPayment && paymentMethod === "stripe" && (
                        <Elements stripe={stripePromise}>
                            <form onSubmit={handleStripePayment}>
                                <CardElement />
                                <button type="submit">Payer avec Stripe</button>
                            </form>
                        </Elements>
                    )}

                    <button onClick={() => setShowPayment(true)} className="place-order-btn">
                        Passer la commande
                    </button>
                </div>
            </div>
        </div>
    );
};

export default OrderPage;
