import React, { useState, useContext, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { CartContext } from "../context/CartContext";
import { UserContext } from "../context/UserContext";
import { PayPalScriptProvider, PayPalButtons } from "@paypal/react-paypal-js";
import "../styles/OrderPage.css";

const OrderPage = () => {
    const { cart, clearCart } = useContext(CartContext);
    const { user } = useContext(UserContext);
    const [address, setAddress] = useState(null);
    const [firstName, setFirstName] = useState("");
    const [lastName, setLastName] = useState("");
    const [phone, setPhone] = useState("");
    const [addressText, setAddressText] = useState("");
    const [postalCode, setPostalCode] = useState("");
    const [isAddressSaved, setIsAddressSaved] = useState(false);
    const [showPaymentForm, setShowPaymentForm] = useState(false);
    const [paymentMethod, setPaymentMethod] = useState("");
    const navigate = useNavigate();

    const totalAmount = cart.reduce((acc, item) => acc + item.prix * item.quantity, 0);

    useEffect(() => {
        if (user) {
            axios.get(`http://localhost:5001/api/addresses/${user.id}`)
                .then((response) => {
                    if (response.data) {
                        setAddress(response.data);
                        setIsAddressSaved(true);
                        setFirstName(response.data.first_name);
                        setLastName(response.data.last_name);
                        setPhone(response.data.phone);
                        setAddressText(response.data.address);
                        setPostalCode(response.data.postal_code);
                    }
                })
                .catch((error) => console.error("Erreur chargement de l'adresse :", error));
        }
    }, [user]);

    const handlePaymentSuccess = () => {
        alert("Paiement rÃ©ussi !");
        clearCart();
        navigate("/");
    };

    const handlePayPalPayment = async () => {
        try {
            const response = await axios.post("http://localhost:5001/api/checkout/create-payment", {
                amount: (totalAmount * 1.15).toFixed(2),
            });

            if (response.data.approvalUrl) {
                window.location.href = response.data.approvalUrl;
            } else {
                alert("Erreur lors de la crÃ©ation du paiement avec PayPal");
            }
        } catch (error) {
            console.error("Erreur lors du paiement avec PayPal :", error);
            alert("Erreur lors du paiement avec PayPal");
        }
    };

    return (
        <div className="order-page">
            <h2>Passer Ã  la caisse ðŸ‡¨ðŸ‡¦</h2>
            <div className="order-container">
                {isAddressSaved && (
                    <div className="order-right">
                        <h3>Total de la commande</h3>
                        <p>Sous-total : {totalAmount.toFixed(2)} CAD</p>
                        <p>Livraison : GRATUIT</p>
                        <p>Taxes : {(totalAmount * 0.15).toFixed(2)} CAD</p>
                        <p>Total estimÃ© : {(totalAmount * 1.15).toFixed(2)} CAD</p>
                        <h4>Choisissez votre mÃ©thode de paiement :</h4>
                        <button onClick={handlePayPalPayment} className="btn-paypal">Payer avec PayPal</button>
                        {showPaymentForm && paymentMethod === "paypal" && (
                            <PayPalScriptProvider options={{ "client-id": process.env.REACT_APP_PAYPAL_CLIENT_ID }}>
                                <PayPalButtons
                                    createOrder={(data, actions) => actions.order.create({
                                        purchase_units: [{
                                            amount: {
                                                value: (totalAmount * 1.15).toFixed(2),
                                                currency_code: "CAD",
                                            }
                                        }]
                                    })}
                                    onApprove={(data, actions) => actions.order.capture().then(handlePaymentSuccess)}
                                />
                            </PayPalScriptProvider>
                        )}
                    </div>
                )}
            </div>
        </div>
    );
};

export default OrderPage;
