import React, { useState, useEffect } from "react";
import axios from "axios";
import ProductCard from "../components/ProductCard";
import "../styles/BeautyPage.css";
import { useNavigate } from "react-router-dom";

const BeautyPage = () => {
    const [products, setProducts] = useState([]);
    const [loading, setLoading] = useState(true);
    const [cart, setCart] = useState([]);
    const [likedProducts, setLikedProducts] = useState([]);
    const navigate = useNavigate();


    useEffect(() => {
        const storedLikes = JSON.parse(localStorage.getItem("likedProducts")) || [];
        setLikedProducts(storedLikes);
    }, []);

    useEffect(() => {
        axios
            .get("http://localhost:5001/api/products")
            .then((response) => {
                console.log("ðŸ“¦ Produits reÃ§us depuis l'API :", response.data);
                setProducts(response.data);
                setLoading(false);
            })
            .catch((error) => {
                console.error("âŒ Erreur chargement des produits :", error.message);
                setLoading(false);
            });
    }, []);
    

    const addToCart = (product) => {
        if (!cart.some((p) => p.id === product.id)) {
            setCart([...cart, product]);
            console.log("ðŸ›’ Produit ajoutÃ© :", product);
        } else {
            console.log("âš ï¸ Produit dÃ©jÃ  dans le panier !");
        }
    };

    const toggleLike = (productId) => {
        let updatedLikes;
        if (likedProducts.includes(productId)) {
            updatedLikes = likedProducts.filter(id => id !== productId);
        } else {
            updatedLikes = [...likedProducts, productId];
        }
        setLikedProducts(updatedLikes);
        localStorage.setItem("likedProducts", JSON.stringify(updatedLikes)); // âœ… Sauvegarde locale
    };

    return (
        <div className="beauty-page">
            <h2 className="section-title">Nos Produits BeautÃ©</h2>
    
            {loading ? (
                <p>Chargement des produits...</p>
            ) : products.length > 0 ? (
                <div className="product-grid">
                    {products.map((product) => (
                        <div key={product.id} className="product-card">
                            <img src={`http://localhost:5001${product.image_url}`} alt={product.nom} />
                            <h3>{product.nom}</h3>
                            <p>{product.prix} CAD</p>
                            <div className="buttons">
                                <button onClick={() => addToCart(product)}>ðŸ›’</button>
                                <button onClick={() => navigate(`/product/${product.id}`)}>Voir plus</button>
                            </div>
                        </div>
                    ))}
                </div>
    
            ) : (
                <p className="no-products">Aucun produit disponible.</p>
            )}
        </div>
    );
    
};

export default BeautyPage;
