import React, { useState, useContext } from "react";
import { Link, useNavigate } from "react-router-dom";
import { FaUserCircle, FaHeart, FaShoppingCart } from "react-icons/fa";
import "../styles/Navbar.css";
import { UserContext } from "../context/UserContext";
import { FavoritesContext } from "../context/FavoritesContext";
import { CartContext } from "../context/CartContext";

const Navbar = ({ onSearch }) => {
    const { user, logout } = useContext(UserContext);
    const { favorites } = useContext(FavoritesContext);
    const { cart } = useContext(CartContext);
    const [searchTerm, setSearchTerm] = useState("");
    const [menuOpen, setMenuOpen] = useState(false);
    const navigate = useNavigate();

    const handleSearch = (event) => {
        const term = event.target.value;
        setSearchTerm(term);
        if (onSearch) {
            onSearch(term); // Appeler la fonction onSearch si elle est d√©finie
        }
    };

    const handleLogout = () => {
        logout();
        setMenuOpen(false);
        navigate("/login");
    };

    return (
        <nav className="navbar">
            <Link to="/" className="logo">Lys√©a</Link>
            <div className="nav-links">
                <Link to="/BeautyPage">Beaut√©</Link>
                <Link to="/lifestyle">Lifestyle</Link>
                <Link to="/blog">Blog</Link>
            </div>

            <div className="nav-icons">
                {/* üîç Barre de recherche */}
                <div className="search-bar">
                    <input
                        type="text"
                        value={searchTerm}
                        onChange={handleSearch}
                        placeholder="Rechercher..."
                    />
                </div>

                {/* ‚ù§Ô∏è Ic√¥ne Favoris */}
                <Link to="/favorites" className="fav-link">
                    <FaHeart className="icon" />
                    {favorites.length > 0 && <span className="fav-badge">{favorites.length}</span>}
                </Link>

                {/* üõí Ic√¥ne Panier */}
                <Link to="/cart" className="cart-link">
                    <FaShoppingCart className="icon" />
                    {cart.length > 0 && <span className="cart-badge">{cart.length}</span>}
                </Link>

                {/* üë§ Ic√¥ne Utilisateur */}
                <div className="user-menu">
                    <FaUserCircle className="icon" onClick={() => setMenuOpen(!menuOpen)} />
                    {menuOpen && (
                        <div className="dropdown">
                            {user ? (
                                <>
                                    <p>Bienvenue, {user.nom}</p>
                                    <button onClick={handleLogout} className="logout-btn">Se d√©connecter</button>
                                </>
                            ) : (
                                <>
                                    <Link to="/login" className="dropdown-item" onClick={() => setMenuOpen(false)}>Se connecter</Link>
                                    <Link to="/register" className="dropdown-item" onClick={() => setMenuOpen(false)}>S'inscrire</Link>
                                </>
                            )}
                        </div>
                    )}
                </div>
            </div>
        </nav>
    );
};

export default Navbar;
